import SwiftUI
import Combine

struct CircleView: View {

 @State var progress: CGFloat = 0
 let timer = Timer.publish(every: 1, on: .main, in: .common).autoconnect()

    var body: some View {
      Circle()
        .trim(from: 0.0, to: progress)
        .stroke(Color.green, lineWidth: 20)
