```swift
extension Color {
  static func generateRandomColor() -> Self {
  enum RColor: Int {
    case red = 0, green = 1, blue = 2, yellow = 3

    var color: Color {
      switch self {
      case .blue: return Color.blue
      case .green: return Color.green
